import { useState, useEffect } from "react";
import './App.css';

function App() {
  const status = useScript(
    "https://feed.fm/js/feed-media-audio-player.1.99.2.min.js"
  );
  console.log(status);
if(status === "ready"){
      let player = new window.Feed.Player('demo', 'demo');
      let playerView = new window.Feed.PlayerView('player-view-div', player);
      player.tune();
      //console.log("test");
}

  return (
    <body style="padding-top: 90px; padding-bottom: 200px; background-color: #ededed">

    <div>
      <div
        class="header fixed-top"
        height="75"
        style="background-color: #163a3f"
      >
        <div class="col-sm-12 mt-2 mb-2 text-center">
          <img src="images/logo.svg" width="150" height="70" alt="" />
          <h2 style="color: white">Web Player</h2>
        </div>
        <hr style="width: 100%; text-align: left; margin-left: 0" />
      </div>
      <form>
        <div class="form-group mx-4">
          <label for="inputToken" class="mb-n1">Token</label>
          <input
            type="password"
            class="form-control mb-n1"
            id="userToken"
            placeholder="Token"
            value="demo"
            style="width: 50%"
          />
        </div>
        <div class="form-group mx-4">
          <label for="inputSecret" class="mb-n1">Secret</label>
          <input
            type="password"
            class="form-control mb-n1"
            id="userSecret"
            placeholder="Secret"
            value="demo"
            style="width: 50%"
          />
        </div>
        <button
          type="button"
          class="btn btn-secondary mx-4 mb-n1"
          onClick="submitUserCredentials()"
        >
          Submit
        </button>
      </form>
      <hr style="width: 100%; text-align: left; margin-left: 0" />
      <div class="form-group mx-4">
        <label for="inputStationSearch">Search for station</label>
        <input
          type="text"
          class="form-control"
          id="inputStationSearch"
          placeholder="Station Name"
          style="width: 50%"
        />
      </div>
      <div
        class="list-group mx-4 mr-5"
        id="station-list-div"
        style="width: 48%"
      ></div>
      <footer
        class="fixed-bottom"
        height="100"
        style="background-color: rgba(255, 255, 255, 1)"
      >
        <hr
          style="
            width: 100%;
            text-align: left;
            margin-left: 0;
            margin-bottom: 1;
            margin-top: 0;
          "
        />

        <div class="mx-4" id="player-view-div" style="width: 95%">
          <div class="status"></div>
          <div style="height: 20px; background-color: rgba(94, 94, 94, 0.3)">
            <div
              class="h-100 d-inline-block progress"
              style="background-color: rgba(123, 192, 166, 1)"
            ></div>
          </div>
          <div>
            <label class="elapsed"></label> /
            <label class="duration"></label>
          </div>
          <button class="play-button btn btn-secondary">play</button>
          <button class="pause-button btn btn-secondary">pause</button>
          <button class="skip-button btn btn-secondary">skip</button>
		  <button class="btn btn-secondary" onClick="resetStation()">reset</button>
        </div>

        <div class="container text-center">
          <div class="row">
            <div class="col-12">
              <p style="font-size: 50%">
                Copyright Â© Feed Media Group. All rights reserved.
              </p>
            </div>
          </div>
        </div>
      </footer>
    </div>
    </body>
  );
}
function useScript(src) {
  // Keep track of script status ("idle", "loading", "ready", "error")
  const [status, setStatus] = useState(src ? "loading" : "idle");

  useEffect(
    () => {
      // Allow falsy src value if waiting on other data needed for
      // constructing the script URL passed to this hook.
      if (!src) {
        setStatus("idle");
        return;
      }
      // Fetch existing script element by src
      // It may have been added by another intance of this hook
      let script = document.querySelector(`script[src="${src}"]`);

      if (!script) {
        // Create script

        script = document.createElement("script");
        script.src = src;
        script.async = true;
        script.setAttribute("data-status", "loading");
        // Add script to document body
        document.body.appendChild(script);

        // Store status in attribute on script
        // This can be read by other instances of this hook
        const setAttributeFromEvent = (event) => {
          script.setAttribute(
            "data-status",
            event.type === "load" ? "ready" : "error"
          );
        };
        script.addEventListener("load", setAttributeFromEvent);
        script.addEventListener("error", setAttributeFromEvent);
      } else {
        // Grab existing script status from attribute and set to state.
        setStatus(script.getAttribute("data-status"));
      }
      // Script event handler to update status in state
      // Note: Even if the script already exists we still need to add
      // event handlers to update the state for *this* hook instance.
      const setStateFromEvent = (event) => {
        setStatus(event.type === "load" ? "ready" : "error");
      };
      // Add event listeners
      script.addEventListener("load", setStateFromEvent);
      script.addEventListener("error", setStateFromEvent);
      // Remove event listeners on cleanup
      return () => {
        if (script) {
          script.removeEventListener("load", setStateFromEvent);
          script.removeEventListener("error", setStateFromEvent);
        }
      };
    },
    [src] // Only re-run effect if script src changes
  );
  return status;
}

export default App;
